import React, { useState, useEffect } from 'react';
import { Button } from './ui/button';
import { toast } from 'sonner';
import { X, Download, Trash2, Play } from 'lucide-react';
import { apiV1 } from '../services/api-v1';

const MediaGallery = ({ ayamId, ayamType }) => {
  const [media, setMedia] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [showLightbox, setShowLightbox] = useState(false);

  useEffect(() => {
    loadMedia();
  }, [ayamId, ayamType]);

  useEffect(() => {
    const handleRefresh = () => {
      loadMedia();
    };

    window.addEventListener('media-refresh', handleRefresh);
    return () => {
      window.removeEventListener('media-refresh', handleRefresh);
    };
  }, [ayamId, ayamType]);

  const loadMedia = async () => {
    setLoading(true);
    try {
      const data = await apiV1.getAyamMedia(ayamId, ayamType);
      setMedia(data);
    } catch (error) {
      console.error('Load media error:', error);
      setMedia([]);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (mediaId, fileName) => {
    if (window.confirm(`Hapus ${fileName}?`)) {
      try {
        await apiV1.deleteAyamMedia(mediaId);
        setMedia(media.filter(m => m.id !== mediaId));
        toast.success('Media berhasil dihapus');
      } catch (error) {
        console.error('Delete error:', error);
        toast.error('Gagal hapus media');
      }
    }
  };

  const handleDownload = (fileUrl, fileName) => {
    const link = document.createElement('a');
    link.href = fileUrl;
    link.download = fileName;
    link.click();
  };

  if (loading) return <div className="text-center py-4 text-gray-500">Loading...</div>;

  if (media.length === 0) {
    return <div className="text-center py-8 text-gray-500">Belum ada media</div>;
  }

  const currentMedia = media[selectedIndex];

  return (
    <div className="space-y-4">
      {showLightbox && currentMedia && (
        <div className="fixed inset-0 bg-black z-50 flex items-center justify-center p-4" style={{backgroundColor: 'rgba(0,0,0,0.9)'}}>
          <div className="relative max-w-4xl w-full">
            <button
              onClick={() => setShowLightbox(false)}
              className="absolute -top-10 right-0 text-white hover:text-gray-300"
            >
              <X className="w-6 h-6" />
            </button>

            {currentMedia.file_type && currentMedia.file_type.startsWith('image/') ? (
              <img src={currentMedia.file_url} alt={currentMedia.file_name} className="w-full h-auto" />
            ) : (
              <video src={currentMedia.file_url} controls autoPlay className="w-full h-auto" />
            )}

            <div className="flex justify-between mt-4 text-white text-sm">
              <button
                onClick={() => setSelectedIndex(Math.max(0, selectedIndex - 1))}
                disabled={selectedIndex === 0}
                className="disabled:opacity-50"
              >
                Prev
              </button>
              <span>{selectedIndex + 1} / {media.length}</span>
              <button
                onClick={() => setSelectedIndex(Math.min(media.length - 1, selectedIndex + 1))}
                disabled={selectedIndex === media.length - 1}
                className="disabled:opacity-50"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
        {media.map((m, index) => (
          <div
            key={m.id}
            className="relative group cursor-pointer rounded overflow-hidden bg-gray-200"
          >
            <div className="aspect-square relative" onClick={() => { setSelectedIndex(index); setShowLightbox(true); }}>
              {m.file_type && m.file_type.startsWith('image/') ? (
                <img src={m.file_url} alt={m.file_name} className="w-full h-full object-cover" />
              ) : (
                <div className="w-full h-full flex items-center justify-center bg-gray-800">
                  <Play className="w-8 h-8 text-white" />
                </div>
              )}

              <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-50 transition flex items-center justify-center space-x-2">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDownload(m.file_url, m.file_name);
                  }}
                  className="p-2 bg-white hover:bg-gray-100 rounded"
                  title="Download"
                >
                  <Download className="w-4 h-4 text-gray-800" />
                </button>

                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete(m.id, m.file_name);
                  }}
                  className="p-2 bg-red-500 hover:bg-red-600 rounded"
                  title="Delete"
                >
                  <Trash2 className="w-4 h-4 text-white" />
                </button>
              </div>
            </div>

            <div className="mt-2 p-2">
              <p className="truncate text-xs font-medium">{m.file_name}</p>
              <p className="text-gray-400 text-xs">
                {m.upload_date ? new Date(m.upload_date).toLocaleDateString('id-ID') : '-'}
              </p>
            </div>
          </div>
        ))}
      </div>

      <div className="text-xs text-gray-500 text-center">
        Total: {media.length} media
      </div>
    </div>
  );
};

export default MediaGallery;
